	public function majBddScfinP($scUser, $dom1, $game, $partie)
	{		
		include_once'ScUserFonctionsFinPartie.php';
		$scP=$partie->getScore();
		$partie->setValid(true);				
		$testDay=TestScDayAndWeek($scUser, $dom1, $game, $scP);
		$scTot=MajScTot($scUser, $dom1, $game, $scP);
		MajScMoy($scUser,$dom1, $game, $scTot);
		if($testDay==1)// Si meilleur score du jour
		{
			TestScMax($scUser, $dom1, $game, $scP, $partie->getDate());
			if($game=='MasterQuizz'){MajsumTop5($scUser, $scP);}
			elseif($game=='MediaQuizz'){MajTM($scUser, $dom1, $scP);}
			MajKingMaster($scUser);
		}
		return 1;
	}
	
		
	public function majClassement($tabUsers, $clsmt, $tabMaitres)
	{
		include_once'ScUserFonctionsMajQuot.php';
		$i=0;$j=0;$sc=0;$tab1[0]=null;$nbExae=0;$m=0;$n=0;//$h=0;
			//$i : variable boucle, $j: variable rang =$i sauf si égalité.
			//$sc pour gérer les situations d'égalités de score
			//$nbExae : nb de joueur exaeco - pas indispensable.
			//$m devient positif dès que un user peut s'incrire dans tab1.
			//$n devient positif qd tests d'aglités terminés, pour ne pas tester pour tous les joueurs suivants.
			foreach($tabUsers as $scUser){
				$i++;
				$j=testEqual($clsmt, $scUser, $i, $j, $sc);
				majHighScore($clsmt, $scUser, $j);
				if($j<11){majMedailles($clsmt, $scUser, $j);}
			//	if($i==1 and in_array($scUser, $tabMaitres)!=true){$tab1[0]=$scUser;$m=1;}
				if($m==0 and in_array($scUser, $tabMaitres)!=true){$tab1[0]=$scUser;$m=1;}
			//	elseif($j==$h and $nbExae==($i-2) and in_array($scUser, $tabMaitres)!=true){array_push($tab1,$scUser);$nbExae++;}
				elseif($m==1 and $n==0 and $j!=$i and in_array($scUser, $tabMaitres)!=true){array_push($tab1,$scUser);$nbExae++;}
			//	elseif($m==1 and $n==0 and $j!=$i and in_array($scUser, $tabMaitres)=true){$n=0;}
				elseif($m==1 and $n==0 and $j==$i){$n=1;}// Bien garder le $j==$i, comme ça si l'user est déjà ans la tab maitre et que égalité de score, $n ne passe pas à 1.
				//pour les suivant, tester aussi que $scUser n'appartient pas à tabMaitres.		
		
				calcOldScore($clsmt, $scUser);
				remiseAzero($clsmt, $scUser);
				
			//	$h=$j;
			}
			$tabMaster=majTabMaitres($clsmt, $tabMaitres, $nbExae, $tab1);
			
			return $tabMaster;
		
	}
